using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata.Builders;namespace ContactdocIO.Infrastructure.Persistence.Configurations;public class ContactConfiguration : IEntityTypeConfiguration<Contact>{    public void Configure(EntityTypeBuilder<Contact> builder)    {        builder.HasOne(t => t.OwnedByUser).WithMany(a => a.Contacts).HasForeignKey(a => a.OwnedByUserId);        builder.HasIndex(p => new { p.ContactGroupId, p.Version }).IsUnique();    }}