using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata.Builders;namespace ContractdocIO.Infrastructure.Persistence.Configurations;public class ContractConfiguration : IEntityTypeConfiguration<Contract>{    public void Configure(EntityTypeBuilder<Contract> builder)    {        builder.HasOne(t => t.OwnedByUser).WithMany(a => a.Contracts).HasForeignKey(a => a.OwnedByUserId);        builder.HasIndex(p => new { p.ContractGroupId, p.Version }).IsUnique();    }}