using Microsoft.EntityFrameworkCore.Metadata.Builders;namespace ContactdocIO.Infrastructure.Persistence.Configurations;public class OptionConfiguration : IEntityTypeConfiguration<Option>{    public void Configure(EntityTypeBuilder<Option> builder)    {        builder.HasOne(t => t.Contact).WithMany(a=>a.Options).HasForeignKey(a=>a.ContactId);        builder.Property(t => t.Content).IsRequired();    }}